// src/context/CartContext.js

import React, { createContext, useState, useEffect, useContext } from 'react';
import { getCartItems, addToCart, removeFromCart, updateCartQuantity } from '../Cart/cartApi';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const items = await getCartItems();
      setCartItems(items.products);
      updateCartCount(items.products);
    } catch (error) {
      console.error('Error fetching cart items:', error);
    }
  };

  const updateCartCount = (items) => {
    const count = items.reduce((total, item) => total + item.quantity, 0);
    setCartCount(count);
  };

  const addItemToCart = async (productId, quantity) => {
    try {
      await addToCart(productId, quantity);
      await fetchCartItems();
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  const removeItemFromCart = async (productId) => {
    try {
      await removeFromCart(productId);
      await fetchCartItems();
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const updateItemQuantity = async (productId, quantity) => {
    try {
      await updateCartQuantity(productId, quantity);
      await fetchCartItems();
    } catch (error) {
      console.error('Error updating cart quantity:', error);
    }
  };

  return (
    <CartContext.Provider value={{ cartItems, cartCount, addItemToCart, removeItemFromCart, updateItemQuantity }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
