import React, { useState } from 'react';
import './Collection.css';
import ProductSidebar from './ProductSidebar';

function ProductLayout({ products }) {
    const [showAlert, setShowAlert] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);

    const handleAddToCart = (product) => {
        const storedItems = JSON.parse(localStorage.getItem('cartItems')) || [];
        const existingProductIndex = storedItems.findIndex(item => item.id === product.id);

        if (existingProductIndex !== -1) {
            storedItems[existingProductIndex].quantity += 1;
        } else {
            storedItems.push({ ...product, quantity: 1 });
        }

        localStorage.setItem('cartItems', JSON.stringify(storedItems));
        window.dispatchEvent(new Event('cartUpdated'));

        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 2500);
    };

    const openSidebar = (product) => {
        setSelectedProduct(product);
    };

    const closeSidebar = () => {
        setSelectedProduct(null);
    };

    return (
        <div className="product-layout">
            <div className="products-grid">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        <img
                            src={process.env.PUBLIC_URL + product.image}
                            alt={product.name}
                            className="product-image"
                            onClick={() => openSidebar(product)}
                        />
                        <div className="product-details">
                            <h3 className="product-name">{product.name}</h3>
                            {product.reviews > 0 && (
                                <div className="product-rating">
                                    {"⭐".repeat(product.rating)}
                                    <span>{product.reviews} review{product.reviews > 1 ? 's' : ''}</span>
                                </div>
                            )}
                            <p className="product-price">{product.price.toFixed(2)} SR</p>
                        </div>
                        <button
                            className="add-to-cart"
                            onClick={() => handleAddToCart(product)}
                        >
                            Add to cart
                        </button>
                    </div>
                ))}
            </div>

            {selectedProduct && (
                <ProductSidebar
                    product={selectedProduct}
                    closeSidebar={closeSidebar}
                    handleAddToCart={() => handleAddToCart(selectedProduct)}
                />
            )}

            {showAlert && (
                <div className="cart-alert">Added to cart successfully</div>
            )}
        </div>
    );
}

export default ProductLayout;
