import React, { useState } from 'react';
import './Collection.css';
import ProductSidebar from './ProductSidebar';

function ProductLayout({ products, expanded }) {
  const [showAlert, setShowAlert] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const addToCart = async (product) => {
    try {
      const response = await fetch('/cart/add', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ productId: product.id, quantity: 1 }),
      });

      if (!response.ok) {
        throw new Error(`Error adding item to cart: ${response.statusText}`);
      }

      const updatedCart = await response.json();
      console.log('Cart updated:', updatedCart); // For debugging
      setShowAlert(true);
      setTimeout(() => setShowAlert(false), 2500);
    } catch (error) {
      console.error('Error adding item to cart:', error);
    }
  };

  const openSidebar = (product) => {
    setSelectedProduct(product);
  };

  const closeSidebar = () => {
    setSelectedProduct(null);
  };

  return (
    <div className="product-layout">
      <div className="products-grid">
        {products.map(product => (
          <div key={product.id} className="product-card">
            <img
              src={process.env.PUBLIC_URL + product.image}
              alt={product.name}
              className="product-image"
              onClick={() => openSidebar(product)}
            />
            <div className="product-details">
              <h3 className="product-name">{product.name}</h3>
              {product.reviews > 0 && (
                <div className="product-rating">
                  {"⭐".repeat(product.rating)}
                  <span>{product.reviews} review{product.reviews > 1 ? "s" : ""}</span>
                </div>
              )}
              <p className="product-price">{product.price} SR</p>
              <button
                className="add-to-cart-button"
                onClick={() => addToCart(product)}
              >
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
      {selectedProduct && (
        <ProductSidebar
          product={selectedProduct}
          onClose={closeSidebar}
          addToCart={addToCart}
        />
      )}
      {showAlert && (
        <div className="alert-message">
          Successfully added to cart
        </div>
      )}
    </div>
  );
}

export default ProductLayout;
