import React, { useState } from 'react';
import './Collection.css';

function ProductLayout({ products, expanded }) {
    const [showAlert, setShowAlert] = useState(false);

    const addToCart = (product) => {
        const existingCartItems = JSON.parse(localStorage.getItem('cartItems')) || [];
        const existingItemIndex = existingCartItems.findIndex(item => item.id === product.id);

        if (existingItemIndex !== -1) {
            existingCartItems[existingItemIndex].quantity += 1;
        } else {
            existingCartItems.push({...product, quantity: 1});
        }

        localStorage.setItem('cartItems', JSON.stringify(existingCartItems));

        window.dispatchEvent(new Event('cartUpdated'));
        window.dispatchEvent(new Event('itemAddedToCart'));

        setShowAlert(true);
        setTimeout(() => setShowAlert(false), 3000);
    };

    return (
        <div className="product-layout">
            <div className="products-grid">
                {products.map(product => (
                    <div key={product.id} className="product-card">
                        <img src={process.env.PUBLIC_URL + product.image} alt={product.name} className="product-image" />
                        <div className="product-details">
                            <h3 className="product-name">{product.name}</h3>
                            {product.reviews > 0 && (
                                <div className="product-rating">
                                    {"⭐".repeat(product.rating)}
                                    <span>{product.reviews} review{product.reviews > 1 ? "s" : ""}</span>
                                </div>
                            )}
                            <p className="product-price">{product.price}</p>
                            <button
                                className="add-to-cart-button"
                                onClick={() => addToCart(product)}
                            >
                                Add to Cart
                            </button>
                        </div>
                    </div>
                ))}
            </div>
            {showAlert && (
                <div className="alert-message">
                    Successfully added to cart
                </div>
            )}
        </div>
    );
}

export default ProductLayout;
