import React, { useContext, useState } from 'react';
import axios from 'axios';
import { CartContext } from '../context/CartContext';

const Cart = () => {
  const { cart, loading, error } = useContext(CartContext);
  const [quantity, setQuantity] = useState(1);

  // Add to Cart
  const addToCart = async (productId) => {
    try {
      await axios.post('/cart/add', { productId, quantity });
      alert('Product added to cart');
    } catch (err) {
      alert(err.response.data.error);
    }
  };

  // Remove from Cart
  const removeFromCart = async (productId) => {
    try {
      await axios.delete(`/cart/remove/${productId}`);
      alert('Product removed from cart');
    } catch (err) {
      alert(err.response.data.error);
    }
  };

  // Update Cart Quantity
  const updateCartQuantity = async (productId, newQuantity) => {
    try {
      await axios.put('/cart/update', { productId, quantity: newQuantity });
      alert('Cart updated');
    } catch (err) {
      alert(err.response.data.error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h2>Your Cart</h2>
      {cart && cart.products.length > 0 ? (
        <ul>
          {cart.products.map(item => (
            <li key={item.product._id}>
              <span>{item.product.name}</span>
              <span>Quantity: {item.quantity}</span>
              <span>Price: ${item.product.price}</span>
              <input
                type="number"
                value={quantity}
                onChange={e => setQuantity(e.target.value)}
              />
              <button onClick={() => updateCartQuantity(item.product._id, quantity)}>Update Quantity</button>
              <button onClick={() => removeFromCart(item.product._id)}>Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty</p>
      )}
    </div>
  );
};

export default Cart;
