import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';
import EmptyCart from './EmptyCart';

function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCartItems = async () => {
      try {
        const response = await fetch('/cart/items');

        if (!response.ok) {
          throw new Error(`Error fetching cart items: ${response.statusText}`);
        }

        const cartItems = await response.json();
        setCartItems(cartItems.products);
      } catch (error) {
        console.error('Error fetching cart items:', error);
      }
    };

    fetchCartItems();
    window.addEventListener('itemAddedToCart', handleItemAdded);

    return () => {
      window.removeEventListener('itemAddedToCart', handleItemAdded);
    };
  }, []);

  const handleItemAdded = () => {
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 3000);
  };

  const removeItem = async (productId) => {
    try {
      const response = await fetch(`/cart/remove/${productId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error(`Error removing item from cart: ${response.statusText}`);
      }

      const updatedCart = await response.json();
      setCartItems(updatedCart.products);
      window.dispatchEvent(new Event('cartUpdated'));
    } catch (error) {
      console.error('Error removing item from cart:', error);
    }
  };

  const updateQuantity = async (productId, newQuantity) => {
    try {
      const response = await fetch('/cart/update', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ productId, quantity: newQuantity }),
      });

      if (!response.ok) {
        throw new Error(`Error updating cart quantity: ${response.statusText}`);
      }

      const updatedCart = await response.json();
      setCartItems(updatedCart.products);
      window.dispatchEvent(new Event('cartUpdated'));
    } catch (error) {
      console.error('Error updating cart quantity:', error);
    }
  };

  const totalPrice = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  const handleCheckout = () => {
    navigate('/checkout');
  };

  if (cartItems.length === 0) {
    return <EmptyCart />;
  }

  return (
    <div className="cart">
      {showPopup && (
        <div className="popup">
          <span className="popup-text">✨ Successfully added to cart! ✨</span>
        </div>
      )}
      <h2>Your cart</h2>
      {cartItems.map((item) => (
        <div key={item.id} className="cart-item">
          <img src={item.image} alt={item.name} />
          <div className="item-details">
            <h3>{item.name}</h3>
            <p>{item.price} SR</p>
            <div className="quantity-controls">
              <button onClick={() => updateQuantity(item.id, Math.max(1, item.quantity - 1))}>-</button>
              <span>{item.quantity}</span>
              <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>+</button>
            </div>
          </div>
          <button className="remove-item" onClick={() => removeItem(item.id)}>Remove</button>
        </div>
      ))}
      <div className="cart-summary">
        <p>Estimated total: {totalPrice.toFixed(2)} SR</p>
        <button className="checkout-button" onClick={handleCheckout}>Check out</button>
      </div>
    </div>
  );
}

export default Cart;
