import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';
import EmptyCart from './EmptyCart';
import API from '../api';

function Cart() {
    const [cartItems, setCartItems] = useState([]);
    const [showPopup, setShowPopup] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        fetchCartItems();
        window.addEventListener('itemAddedToCart', handleItemAdded);
        return () => {
            window.removeEventListener('itemAddedToCart', handleItemAdded);
        };
    }, []);

    const fetchCartItems = async () => {
        try {
            const response = await API.get('/cart/items');
            setCartItems(response.data.products);
        } catch (error) {
            console.error('Error fetching cart items:', error);
        }
    };

    const handleItemAdded = () => {
        setShowPopup(true);
        setTimeout(() => setShowPopup(false), 3000);
        fetchCartItems(); // Refresh cart items
    };

    const removeItem = async (productId) => {
        try {
            await API.delete(`/cart/remove/${productId}`);
            fetchCartItems(); // Refresh cart items
            window.dispatchEvent(new Event('cartUpdated'));
        } catch (error) {
            console.error('Error removing item from cart:', error);
        }
    };

    const updateQuantity = async (productId, newQuantity) => {
        try {
            await API.put('/cart/update', { productId, quantity: newQuantity });
            fetchCartItems(); // Refresh cart items
            window.dispatchEvent(new Event('cartUpdated'));
        } catch (error) {
            console.error('Error updating cart quantity:', error);
        }
    };

    const totalPrice = cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0);

    const handleCheckout = () => {
        navigate('/checkout');
    };

    if (cartItems.length === 0) {
        return <EmptyCart />;
    }

    return (
        <div className="cart">
            {showPopup && (
                <div className="popup">
                    <span className="popup-text">✨ Successfully added to cart! ✨</span>
                </div>
            )}
            <h2>Your cart</h2>
            {cartItems.map((item) => (
                <div key={item.product._id} className="cart-item">
                    <img src={item.product.image} alt={item.product.name} />
                    <div className="item-details">
                        <h3>{item.product.name}</h3>
                        <p>{item.product.price} SR</p>
                        <div className="quantity-controls">
                            <button onClick={() => updateQuantity(item.product._id, Math.max(1, item.quantity - 1))}>-</button>
                            <span>{item.quantity}</span>
                            <button onClick={() => updateQuantity(item.product._id, item.quantity + 1)}>+</button>
                        </div>
                    </div>
                    <button className="remove-item" onClick={() => removeItem(item.product._id)}>Remove</button>
                </div>
            ))}
            <div className="cart-summary">
                <p>Estimated total: {totalPrice.toFixed(2)} SR</p>
                <button className="checkout-button" onClick={handleCheckout}>Check out</button>
            </div>
        </div>
    );
}

export default Cart;
