import React, { createContext, useReducer, useEffect } from 'react';
import axios from 'axios';

// Initial state of the cart
const initialState = {
    cart: null,
    loading: true,
    error: null,
};

// Create a reducer to manage cart actions
const cartReducer = (state, action) => {
    switch (action.type) {
        case 'CART_LOADED':
            return { ...state, cart: action.payload, loading: false };
        case 'CART_ERROR':
            return { ...state, error: action.payload, loading: false };
        default:
            return state;
    }
};

// Create CartContext
export const CartContext = createContext();

// CartContext provider
export const CartProvider = ({ children }) => {
    const [state, dispatch] = useReducer(cartReducer, initialState);

    // Load cart when the app starts
    useEffect(() => {
        const fetchCart = async () => {
            try {
                const res = await axios.get('/cart/items');
                dispatch({ type: 'CART_LOADED', payload: res.data });
            } catch (err) {
                dispatch({ type: 'CART_ERROR', payload: err.response.data.error });
            }
        };

        fetchCart();
    }, []);

    return (
        <CartContext.Provider value={{ cart: state.cart, loading: state.loading, error: state.error }}>
            {children}
        </CartContext.Provider>
    );
};
